package basicprogrammes;

public class Forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   
    
    	for(int i=1; i<20; i++)
    	{
    	System.out.println(i);
    	i=i+1;
    }
    	{
    		System.out.println("*********Odd Number*****");
    		
    	}
    	{
    		for(int i=50; i>0; i--)
    			if((i%2==0))
    			{
    			System.out.println(i);
    	}
	}
	}
}
